struct Vec2f {
    public double X { get; private set; }
    public double Y { get; private set; }

    public Vec2f(double x, double y) : this() {
        X = x;
        Y = y;
    }

    public override string ToString() {
        return string.Format("({0}, {1})", X, Y);
    }

    public static Vec2f operator+(Vec2f a, Vec2f b) {
        return new Vec2f(a.X + b.X, a.Y + b.Y);
    }

    public static Vec2f operator-(Vec2f a, Vec2f b) {
        return new Vec2f(a.X - b.X, a.Y - b.Y);
    }

    public static Vec2f operator*(Vec2f a, double t) {
        return new Vec2f(a.X * t, a.Y * t);
    }

    public static Vec2f operator*(double t, Vec2f a) {
        return new Vec2f(a.X * t, a.Y * t);
    }

    // a???b = |a||b|cos??
    public double Dot(Vec2f other) {
        return X * other.X + Y * other.Y;
    }

    public double Cross(Vec2f other) {
        return X * other.Y - Y * other.X;
    }

    public Vec2f Normalized() {
        var m = Magnitude();
        if (m != 0 && m != 1) {
            return new Vec2f(X / m, Y / m);
        }
        return this;
    }

    public double Magnitude() {
        return Math.Sqrt(X * X + Y * Y);
    }

    public double SqrMagnitude() {
        return X * X + Y * Y;
    }

    public static double Distance(Vec2f a, Vec2f b) {
        return (a - b).Magnitude();
    }
}

using System;

class Program
{
  static readonly int COUNTER_CLOCKWISE = 1;
  static readonly int CLOCKWISE = -1;
  static readonly int ONLINE_BACK = 2;
  static readonly int ONLINE_FRONT = -2;
  static readonly int ON_SEGMENT = 0;

  public class Vector
  {
    public double x, y;
    public Vector(double x, double y)
    {
      this.x = x;
      this.y = y;
    }

    public static Vector operator-(Vector v1, Vector v2)
    {

      return new Vector(v1.x-v2.x, v1.y-v2.y);
    }
  }
  public static double dot(Vector v1, Vector v2)
  {

    return v1.x*v2.x + v1.y*v2.y;
  }

  public static double cross(Vector v1, Vector v2)
  {

    return v1.x*v2.y - v1.y*v2.x;
  }

  public static double norm(Vector v)
  {

    return v.x*v.x + v.y*v.y;
  }

  public static int ccw(Vector p1, Vector p2, Vector p3)
  {
    Vector a = p2 - p1;
    Vector b = p3 - p1;
    if(cross(a, b) > Double.Epsilon) { return COUNTER_CLOCKWISE; }
    if(cross(a, b) < -Double.Epsilon) { return CLOCKWISE; }
    if(dot(a, b)< -Double.Epsilon) { return ONLINE_BACK; }
    if(norm(a) < norm(b)) { return ONLINE_FRONT; }
    return ON_SEGMENT;
  }

  public static void Main()
  {
    string[] s = Console.ReadLine().Split(' ');
    Vector v1 = new Vector(int.Parse(s[0]), int.Parse(s[1]));
    Vector v2 = new Vector(int.Parse(s[2]), int.Parse(s[3]));

    int n = int.Parse(Console.ReadLine());
