namespace CSharpSpiral {
    class Program {
        static void Main(string[] args) {
            var sw = new StreamWriter(Console.OpenStandardOutput()) { AutoFlush = false };
            Console.SetOut(sw);
            new Program().Solve(new ConsoleInput(Console.In, ' '));
            Console.Out.Flush();
        }
		public void Solve(ConsoleInput cin) {
			var line = cin.ReadLongArray(4);
			long x1 = line[0], y1 = line[1], x2 = line[2], y2 = line[3];
			long dx = x2 - x1, dy = y2 - y1;
			var lenSq = dx * dx + dy * dy;
			var q = cin.ReadInt;
			for(int i = 0; i < q; i++) {
				var p = cin.ReadLongArray(2);
				var px = p[0] - x1;
				var py = p[1] - y1;
				var outer = px * dy - py * dx;
				string state;
				switch(outer.CompareTo(0)) {
					case 1:
						state = "CLOCKWISE";
						break;
					case -1:
						state = "COUNTER_CLOCKWISE";
						break;
					default:
						var inner = px * dx + py * dy;
						if(inner < 0) state = "ONLINE_BACK";
						else {
							var lenSqP = px * px + py * py;
							if(lenSqP > lenSq) state = "ONLINE_FRONT";
							else state = "ON_SEGMENT";
						}
						break;
				}
				Console.WriteLine(state);
			}
		}
	}
	static class ExtendClass {
		public static string Join<T>(this IEnumerable<T> X, string separator = " ") => string.Join(separator, X);
	}
	class ConsoleInput {
		private readonly TextReader _stream;
		private readonly char _separator = ' ';
		private readonly Queue<string> inputStream;
		public ConsoleInput(TextReader stream, char separator = ' ') {
			this._separator = separator;
			this._stream = stream;
			inputStream = new Queue<string>();
		}
		public string Read {
			get {
				if(inputStream.Count != 0) return inputStream.Dequeue();
				string[] tmp = _stream.ReadLine().Split(_separator);
				for(int i = 0; i < tmp.Length; ++i)
					inputStream.Enqueue(tmp[i]);
				return inputStream.Dequeue();
			}
		}
		public string ReadLine { get { return _stream.ReadLine(); } }
		public int ReadInt { get { return int.Parse(Read); } }
		public long ReadLong { get { return long.Parse(Read); } }
		public double ReadDouble { get { return double.Parse(Read); } }
		public string[] ReadStrArray(long N) {
			var ret = new string[N];
			for(long i = 0; i < N; ++i)
				ret[i] = Read;
			return ret;
		}
		public int[] ReadIntArray(long N) { var ret = new int[N]; for(long i = 0; i < N; ++i) ret[i] = ReadInt; return ret; }
		public long[] ReadLongArray(long N) { var ret = new long[N]; for(long i = 0; i < N; ++i) ret[i] = ReadLong; return ret; }
		public double[] ReadDoubleArray(long N) { var ret = new double[N]; for(long i = 0; i < N; ++i) ret[i] = ReadDouble; return ret; }
	}
}

