                int[] x_y = Array.ConvertAll(Console.ReadLine().Split(' '), int.Parse);

                Vector v = new Vector(x_y[0], x_y[1]) - v1;
                Ccw ccw = Vector.CountrClockWise(baseV, v);

                Console.WriteLine("{0}", ConvertCcwToStr(ccw));
            }
        }

