namespace ConsoleApplication1
{
    enum CCW { COUNTER_CLOCKWISE = 1, CLOCKWISE = -1, ONLINE_BACK = 2, ONLINE_FRONT = -2, ON_SEGMENT = 0 }

    class Program
    {
        static void Main(string[] args)
        {
            double[] input = Array.ConvertAll(Console.ReadLine().Split(' '), double.Parse);
            Pt a = new Pt(input[0], input[1]);
            Pt b = new Pt(input[2], input[3]);

            int q = int.Parse(Console.ReadLine());

            StringBuilder sb = new StringBuilder();
            for (int i = 0; i < q; i++)
            {
                input = Array.ConvertAll(Console.ReadLine().Split(' '), double.Parse);
                Pt p = new Pt(input[0], input[1]);

                sb.AppendLine(CheckCCW(a, b, p).ToString());
            }
            Console.Write(sb);
        }

        static CCW CheckCCW(Pt p0, Pt p1, Pt p2)
        {
            Pt a = p1 - p0;
            Pt b = p2 - p0;

            if (a.Cross(b) > 0) return CCW.COUNTER_CLOCKWISE;
            if (a.Cross(b) < 0) return CCW.CLOCKWISE;
            if (a.Dot(b) < 0) return CCW.ONLINE_BACK;
            if (a.Norm < b.Norm) return CCW.ONLINE_FRONT;
            return CCW.ON_SEGMENT;
        }
    }

    class Pt : IComparable<Pt>
    {
        static readonly double EPS = 1E-10;

        internal double x, y;

        public Pt(double x, double y)
        {
            this.x = x;
            this.y = y;
        }

        public double Abs { get { return Math.Sqrt(Norm); } }
        public double Norm { get { return x * x + y * y; } }

        public double Dot(Pt p) { return x * p.x + y * p.y; }
        public double Cross(Pt p) { return x * p.y - y * p.x; }

        public static Pt operator +(Pt a, Pt b) { return new Pt(a.x + b.x, a.y + b.y); }
        public static Pt operator -(Pt a, Pt b) { return new Pt(a.x - b.x, a.y - b.y); }
        public static Pt operator *(Pt p, double d) { return new Pt(p.x * d, p.y * d); }
        public static Pt operator /(Pt p, double d) { return new Pt(p.x / d, p.y / d); }
        public static bool operator <(Pt a, Pt b) { return Math.Abs(a.x - b.x) > EPS ? a.x < b.x : a.y < b.y; }
        public static bool operator >(Pt a, Pt b) { return Math.Abs(a.x - b.x) > EPS ? a.x > b.x : a.y > b.y; }
        public static bool operator ==(Pt a, Pt b) { return Math.Abs(a.x - b.x) < EPS && Math.Abs(a.y - b.y) < EPS; }
        public static bool operator !=(Pt a, Pt b) { return Math.Abs(a.x - b.x) >= EPS || Math.Abs(a.y - b.y) >= EPS; }

        public int CompareTo(Pt other)
        {
            return x != other.x ? (int)(x - other.x) : (int)(y - other.y);
        }

        public override bool Equals(object obj)
        {
            Pt other = (Pt)obj;
            return Math.Abs(x - other.x) < EPS && Math.Abs(y - other.y) < EPS;
        }

        public override int GetHashCode()
        {
            return x.GetHashCode() ^ y.GetHashCode();
        }

        public override string ToString()
        {
            return string.Format("{0:f10} {1:f10}", x, y);
        }
    }

    class Seg
    {
        internal Pt p1, p2;

        public Seg(Pt a, Pt b)
        {
            p1 = a;
            p2 = b;
        }
    }
}

using System;
using System.Collections.Generic;
using System.Linq;

namespace CSharpSample01
{

    class Vector2
    {
        public double x;
        public double y;

        public Vector2() { }
        public Vector2(double x, double y)
        {
            this.x = x;
            this.y = y;
        }

        //「射影」
        //点pからベクトルp1p2への射影ベクトルを求める
        public Vector2 Projection(Vector2 p1, Vector2 p2, Vector2 p)
        {

            Vector2 hypo = new Vector2(p.x - p1.x, p.y - p1.y);

            Vector2 ground = new Vector2(p2.x - p1.x, p2.y - p1.y);

            Vector2 shadow = new Vector2
            {
                x = p1.x + ground.x * (hypo.x * ground.x + hypo.y * ground.y) / (ground.x * ground.x + ground.y * ground.y),
                y = p1.y + ground.y * (hypo.x * ground.x + hypo.y * ground.y) / (ground.x * ground.x + ground.y * ground.y)
            };

            return shadow;
        }

        //内積
        public double Dot(Vector2 a, Vector2 b)
        {
            return a.x * b.x + a.y * b.y;
        }

        //外積
        public double Cross(Vector2 a, Vector2 b)
        {
            return a.x * b.y - a.y * b.x;
        }

        //ベクトルの大きさ
        public double Norm(Vector2 a)
        {
            return Math.Sqrt(a.x * a.x + a.y * a.y);
        }


        public string Clockwise(Vector2 p0, Vector2 p1, Vector2 p2)
        {

            string str;

            Vector2 p0p1 = new Vector2(p1.x - p0.x, p1.y - p0.y);
            Vector2 p0p2 = new Vector2(p2.x - p0.x, p2.y - p0.y);

            if (Cross(p0p1, p0p2) > 0)
            {
                str = "COUNTER_CLOCKWISE";
            }
            else if(Cross(p0p1, p0p2) < 0)
            {
                str = "CLOCKWISE";
            }
            else
            {
                double t;

                if(p0p1.x != 0)
                {
                    t = p0p2.x / p0p1.x;
                }
                else
                {
                    t = p0p2.y / p0p1.y;
                }

                if(t < 0)
                {
                    str = "ONLINE_BACK";
                }
                else if(t > 1)
                {
                    str = "ONLINE_FRONT";
                }
                else
                {
                    str = "ON_SEGMENT";
                }

            }

            return str;
        }


    }


    class Program
    {


        static void Main(string[] args)
        {
            double[] data = Console.ReadLine().Split(' ').Select(x => double.Parse(x)).ToArray();

            Vector2 p0 = new Vector2(data[0], data[1]);
            Vector2 p1 = new Vector2(data[2], data[3]);

            int q = int.Parse(Console.ReadLine());

            Vector2 h = new Vector2();

            for(int i = 0; i < q; i++)
            {
                string[] input = Console.ReadLine().Split(' ');
                double s = double.Parse(input[0]);
                double t = double.Parse(input[1]);

                Vector2 p2 = new Vector2(s, t);

                Console.WriteLine(h.Clockwise(p0, p1, p2));

            }


        }
    }
}

