//
func subVector(p1, p2 []float64) []float64 {
	return []float64{
		p1[0] - p2[0],
		p1[1] - p2[1],
	}
}

//
func dotVector(p1, p2 []float64) float64{
	return p1[0] * p2[0] + p1[1] * p2[1]
}

//
func crossVector(p1, p2 []float64) float64{
	return p1[0] * p2[1] - p1[1] * p2[0]
}

//
func absVector(p []float64) float64 {
	return math.Sqrt(p[0] * p[0] + p[1] * p[1])
}

//
func norrmalize(p []float64) float64 {
	return p[0] * p[0] + p[1] * p[1]
}

//
// 1: p1-p2より反時計周り側にp3
// 2: p1-p2より時計周り側にp3
// 3: p1-p2直線上、p2の先にp3
// 4: p1-p2直線上、p1の前にp3
// 5: p1-p2線分上にp3
func checkClockWise(p1, p2, p3 []float64) int {
	p1p2 := subVector(p2, p1)
	p1p3 := subVector(p3, p1)
	if crossVector(p1p2, p1p3) > 0.0 {
		return 1
	} else if crossVector(p1p2, p1p3) < 0.0 {
		return 2
	} else if dotVector(p1p2, p1p3) < 0.0 {
		return 4
	} else if norrmalize(p1p2) < norrmalize(p1p3) {
		return 3
	}
	return 5
}

// solve
func solve(sc *bufio.Scanner) {
	sc.Scan()
	elements := strings.Split(sc.Text(), " ")
	p1 := make([]float64, 2)
	p2 := make([]float64, 2)

	pStr, _ := strconv.Atoi(elements[0])
	p1[0] = float64(pStr)
	pStr, _ = strconv.Atoi(elements[1])
	p1[1] = float64(pStr)
	pStr, _ = strconv.Atoi(elements[2])
	p2[0] = float64(pStr)
	pStr, _ = strconv.Atoi(elements[3])
	p2[1] = float64(pStr)

	sc.Scan()
	n, _ := strconv.Atoi(sc.Text())

	for i := 0; i < n; i++ {
		sc.Scan()
		elements = strings.Split(sc.Text(), " ")

		p3 := make([]float64, 2)

		pStr, _ := strconv.Atoi(elements[0])
		p3[0] = float64(pStr)
		pStr, _ = strconv.Atoi(elements[1])
		p3[1] = float64(pStr)

		result := checkClockWise(p1, p2, p3)

		word := ""
		switch result {
		case 1: word = "COUNTER_CLOCKWISE"
		case 2: word = "CLOCKWISE"
		case 3: word = "ONLINE_FRONT"
		case 4: word = "ONLINE_BACK"
		case 5: word = "ON_SEGMENT"
		}

		fmt.Printf("%s\n", word)
	}
}

