var scanner = bufio.NewScanner(os.Stdin)

func nextString() string {
	scanner.Scan()
	return scanner.Text()
}

func nextInt() int {
	n, err := strconv.Atoi(nextString())
	if err != nil {
		fmt.Printf("strconv.Atoi failed: %v\n", err)
	}
	return n
}

func nextFloat64() (float64, error) {
	return strconv.ParseFloat(nextString(), 64)
}

func norm2(x, y float64) float64 {
	return x*x + y*y
}

func crossProd(x1, y1, x2, y2 float64) float64 {
	return x1*y2 - x2*y1
}

func lte(x1, y1, x2, y2 float64) bool {
	if x1 == x2 {
		return y1 <= y2
	}
	return x1 <= x2
}

func main() {
	scanner.Split(bufio.ScanWords)

	p0x, _ := nextFloat64()
	p0y, _ := nextFloat64()
	p1x, _ := nextFloat64()
	p1y, _ := nextFloat64()

	p1x -= p0x
	p1y -= p0y

	nQueries := nextInt()
	for i := 0; i < nQueries; i++ {
		p2x, _ := nextFloat64()
		p2y, _ := nextFloat64()
		p2x -= p0x
		p2y -= p0y

		cross := crossProd(p1x, p1y, p2x, p2y)
		// fmt.Println(cross)
		if cross > 0 {
			fmt.Println("COUNTER_CLOCKWISE")
		} else if cross < 0 {
			fmt.Println("CLOCKWISE")
		} else {
			if (lte(0, 0, p2x, p2y) && lte(p2x, p2y, p1x, p1y)) || (lte(p1x, p1y, p2x, p2y) && lte(p2x, p2y, 0, 0)) {
				fmt.Println("ON_SEGMENT")
			} else if (lte(0, 0, p1x, p1y) && lte(p1x, p1y, p2x, p2y)) || (lte(p2x, p2y, p1x, p1y) && lte(p1x, p1y, 0, 0)) {
				fmt.Println("ONLINE_FRONT")
			} else if (lte(p2x, p2y, 0, 0) && lte(0, 0, p1x, p1y)) || (lte(p1x, p1y, 0, 0) && lte(0, 0, p2x, p2y)) {
				fmt.Println("ONLINE_BACK")
			}
		}
	}
}

