func getScanner(fp *os.File) *bufio.Scanner {
	scanner := bufio.NewScanner(fp)
	scanner.Split(bufio.ScanWords)
	scanner.Buffer(make([]byte, 1000005), 1000005)
	return scanner
}
func getNextString(scanner *bufio.Scanner) string {
	scanner.Scan()
	return scanner.Text()
}
func getNextInt(scanner *bufio.Scanner) int {
	i, _ := strconv.Atoi(getNextString(scanner))
	return i
}
func getNextInt64(scanner *bufio.Scanner) int64 {
	i, _ := strconv.ParseInt(getNextString(scanner), 10, 64)
	return i
}
func getNextUint64(scanner *bufio.Scanner) uint64 {
	i, _ := strconv.ParseUint(getNextString(scanner), 10, 64)
	return i
}
func getNextFloat64(scanner *bufio.Scanner) float64 {
	i, _ := strconv.ParseFloat(getNextString(scanner), 64)
	return i
}
func main() {
	fp := os.Stdin
	wfp := os.Stdout
	cnt := 0
	if os.Getenv("MASPY") == "ますピ" {
		fp, _ = os.Open(os.Getenv("BEET_THE_HARMONY_OF_PERFECT"))
		cnt = 1
	}
	if os.Getenv("MASPYPY") == "ますピッピ" {
		wfp, _ = os.Create(os.Getenv("NGTKANA_IS_GENIUS10"))
	}
	scanner := getScanner(fp)
	writer := bufio.NewWriter(wfp)
	solve(scanner, writer)
	for i := 0; i < cnt; i++ {
		fmt.Fprintln(writer, "-----------------------------------")
		solve(scanner, writer)
	}
	writer.Flush()
}
func solve(scanner *bufio.Scanner, writer *bufio.Writer) {
	x1 := getNextFloat64(scanner)
	y1 := getNextFloat64(scanner)
	x2 := getNextFloat64(scanner)
	y2 := getNextFloat64(scanner)

	v := complex(x2-x1, y2-y1)
	r, th := cmplx.Polar(v)
	q := getNextInt(scanner)
	eps := 1e-10
	for i := 0; i < q; i++ {
		x := getNextFloat64(scanner) - x1
		y := getNextFloat64(scanner) - y1
		vv := rotate(complex(x, y), -th)
		if imag(vv) > eps {
			fmt.Fprintln(writer, "COUNTER_CLOCKWISE")
			continue
		}
		if imag(vv) < -eps {
			fmt.Fprintln(writer, "CLOCKWISE")
			continue
		}
		r2, th2 := cmplx.Polar(vv)
		if math.Abs(th2) > eps {
			fmt.Fprintln(writer, "ONLINE_BACK")
			continue
		}
		if r2 > r+eps {
			fmt.Fprintln(writer, "ONLINE_FRONT")
			continue
		}
		fmt.Fprintln(writer, "ON_SEGMENT")
	}
}

func rotate(v complex128, theta float64) complex128 {
	r, th := cmplx.Polar(v)
	th += theta
	return cmplx.Rect(r, th)
}

