    auto ab = b - a;
    auto ac = c - a;

    auto d = ab.cross(ac);
    if (d > 0)
        return "COUNTER_CLOCKWISE";
    if (d < 0)
        return "CLOCKWISE";

    // |ab||ac|cos(pi) で cos(pi) = -1 なので負なら逆向き
    if (ab.dot(ac) < 0)
        return "ONLINE_BACK";

    if (ab.magSq() >= ac.magSq())
