CCW = 'COUNTER_CLOCKWISE'
CW = 'CLOCKWISE'
OLB = 'ONLINE_BACK'
OLF = 'ONLINE_FRONT'
OL = 'ON_SEGMENT'


def solve(u, v, a):
    v -= u
    a -= u
    c = v.cross(a)
    if c > 0:
        return CCW
    if c < 0:
        return CW
    d = v.dot(a)
    if d < 0:
        return OLB
    if v.abs2() < a.abs2():
        return OLF
    return OL


