fn ccw(a: Complex, b: Complex, c: Complex) -> i8 {
    let x = b - a;
    let y = c - a;
    if x.cross(y) > 0. {
        return 1; // COUNTER_CLOCKWISE
    }
    if x.cross(y) < 0. {
        return -1; // CLOCKWISE
    }
    if x.dot(y) < 0. {
        return 2; // ONLINE_BACK
    }
    if x.abs() < y.abs() {
        return -2; // ONLINE_FRONT
    };
    return 0; // ON_SEGMENT
}
