        let s = match ccw(p0[0], p0[1], ps[i]) {
            1 => "COUNTER_CLOCKWISE",
            -1 => "CLOCKWISE",
            2 => "ONLINE_BACK",
            -2 => "ONLINE_FRONT",
            0 => "ON_SEGMENT",
            _ => unreachable!(),
        };
        println!("{}", s);
    }
}

use std::f64::consts::PI;

const EPS: f64 = 1e-8;

fn main() {
    let mut sc = Scanner::new();

    let p0 = sc.next::<Pt>();
    let p1 = sc.next::<Pt>();
    let n = sc.next::<usize>();

    for _ in 0..n {
        let p2 = sc.next::<Pt>();

        let ans = match ccw(p1, p0, p2) {
            CCW::CounterClockwise => "COUNTER_CLOCKWISE",
            CCW::Clockwise => "CLOCKWISE",
            CCW::OnLineFront => "ONLINE_FRONT",
            CCW::OnLineBack => "ONLINE_BACK",
            CCW::OnSegment => "ON_SEGMENT",
        };

        println!("{}", ans);
    }
}

pub fn cross(a: Pt, b: Pt) -> f64 {
    (a.conj() * b).im
}

pub fn dot(a: Pt, b: Pt) -> f64 {
    (a.conj() * b).re
}

pub enum CCW {
    CounterClockwise = 1,
    Clockwise = -1,
    OnLineBack = 2,
    OnLineFront = -2,
    OnSegment = 0,
}

pub fn ccw(a: Pt, b: Pt, c: Pt) -> CCW {
    let b = b - a;
    let c = c - a;
    if cross(b, c) > 0.0 {
        CCW::Clockwise
    } else if cross(b, c) < 0.0 {
        CCW::CounterClockwise
    } else if dot(b, c) < 0.0 {
        CCW::OnLineFront
    } else if b.norm() < c.norm() {
        CCW::OnLineBack
    } else {
        CCW::OnSegment
    }
}

