using namespace std;
struct Point { long double px, py; };
long double crs(const Point& a, const Point& b) { return a.px * b.py - a.py * b.px; }
Point crp(Point a1, Point a2, Point b1, Point b2) { Point c; c.px = b2.px - b1.px; c.py = b2.py - b1.py; Point d; d.px = a1.px - b1.px; d.py = a1.py - b1.py; Point e; e.px = a2.px - b1.px; e.py = a2.py - b1.py; Point f; f.px = a2.px - a1.px; f.py = a2.py - a1.py; Point g; g.px = a1.px - f.px; g.py = a1.py - f.py; long double d1 = abs(crs(c, d)); long double d2 = abs(crs(c, e)); g.px *= (d1 / (d1 + d2)); g.py *= (d1 / (d1 + d2)); return g; }
