      cin >> x0 >> y0 >> x1 >> y1;
      seg[j] = Segment(Vector(x0, y0), Vector(x1, y1));
    }
    auto v = cross_point(seg[0], seg[1]);
    printf("%.10f %.10f\n", v.x, v.y);
  }
}

