pair<P,P> geoGetCircleOf2pAndR(P p1,P p2,double r){
	P pc1,pc2, p3;
	double d, l, dx, dy ;

	p3 = (p1+p2)/2.0;
	l = sqDist(p2,p3);

	if  (r*r < l)return pair<P,P>(P(-INF,-INF),P(-INF,-INF));

	d  = sqrt(r*r/l - 1.0) ;
	dx = d * (p2-p3).y ;
	dy = d * (p2-p3).x ;

	pc1.x = p3.x + dx ;
	pc1.y = p3.y - dy ;

	pc2.x = p3.x - dx ;
	pc2.y = p3.y + dy ;

	return pair<P,P>(pc1,pc2);
}

