const double EPS = 1e-12;
inline bool equals(double a, double b) {return fabs(a - b) < EPS;} //判断double相等

struct Point //点
{
	double x, y;
	Point(double x = 0.0, double y = 0.0): x(x), y(y) {}
	void read() {scanf("%lf %lf", &x, &y);}

	Point operator + (Point p) {return Point(x + p.x, y + p.y);}
	Point operator - (Point p) {return Point(x - p.x, y - p.y);}
	Point operator * (double a) {return Point(x * a, y * a);}
	Point operator / (double a) {return Point(x / a, y / a);}

	double norm() {return x * x + y * y;}
	double abs() {return sqrt(norm());}

	bool operator < (const Point &p) const {return !equals(x, p.x) ? x < p.x : y < p.y;}
	bool operator == (const Point &p) const {return fabs(x - p.x) < EPS && fabs(y - p.y) < EPS;}
};
struct Segment //线段
{
	Point p1, p2;
	Segment(Point p1 = (Point){0.0, 0.0}, Point p2 = (Point){0.0, 0.0}): p1(p1), p2(p2) {}
	void read() {p1.read(); p2.read();}
};
typedef Point Vector; //向量
typedef Segment Line; //直线

inline double dot(Vector a, Vector b) {return a.x * b.x + a.y * b.y;} //向量内积
inline double cross(Vector a, Vector b) {return a.x * b.y - a.y * b.x;} //向量外积

inline bool check1(Vector a, Vector b) {return equals(dot(a, b), 0.0);} //判断向量正交
inline bool check2(Vector a, Vector b) {return equals(cross(a, b), 0.0);} //判向量断平行

inline Point project(Line s, Point p) //点到直线的投影
{
	Vector base = s.p2 - s.p1;
	double r = dot(p - s.p1, base) / base.norm();
	return s.p1 + base * r;
}
inline Point reflect(Line s, Point p) {return p + (project(s, p) - p) * 2.0;} //点到直线的映像

inline int ccw(Point p0, Point p1, Point p2) //判断线段p0p1和点p2的关系
{
	Vector a = p1 - p0, b = p2 - p0;
	if(cross(a, b) > EPS) return 1; //逆时针
	if(cross(a, b) < -EPS) return -1; //顺时针
	if(dot(a, b) < -EPS) return 2; //p2->p0->p1
	if(a.norm() < b.norm()) return -2; //p0->p1->p2
	return 0; //p0->p2->p1
}
inline bool intersect(Point p1, Point p2, Point p3, Point p4) //判断线段相交
{
	return ccw(p1, p2, p3) * ccw(p1, p2, p4) <= 0 &&
		   ccw(p3, p4, p1) * ccw(p3, p4, p2) <= 0;
}

inline double dist1(Point a, Point b) {return (a - b).abs();} //两点距离
inline double dist2(Line l, Point p) {return abs(cross(l.p2 - l.p1, p - l.p1) / (l.p2 - l.p1).abs());} //点到直线的距离
inline double dist3(Segment s, Point p) //点到线段的距离
{
	if(dot(s.p2 - s.p1, p - s.p1) < -EPS) return dist1(p, s.p1);
	if(dot(s.p1 - s.p2, p - s.p2) < -EPS) return dist1(p, s.p2);
	return dist2(s, p);
}
inline double dist4(Segment s1, Segment s2) //线段之间的距离
{
	if(intersect(s1.p1, s1.p2, s2.p1, s2.p2)) return 0.0;
	return min(min(dist3(s1, s2.p1), dist3(s1, s2.p2)), min(dist3(s2, s1.p1), dist3(s2, s1.p2)));
}

inline Point CrossPoint(Segment s1, Segment s2) //线段之间的交点
{
	Vector base = s2.p2 - s2.p1;
	double d1 = abs(cross(base, s1.p1 - s2.p1) / base.abs());
	double d2 = abs(cross(base, s1.p2 - s2.p1) / base.abs());
	double t = d1 / (d1 + d2);
	return s1.p1 + (s1.p2 - s1.p1) * t;
}

