struct PT{
	ld X, Y;
	PT (ld X = 0, ld Y = 0) : X(X), Y(Y) {}
	PT operator + (PT ro) const{
		return PT(X+ro.X, Y+ro.Y);
	}
	PT operator - (PT ro) const{
		return PT(X-ro.X, Y-ro.Y);
	}
	PT operator * (ld co) const{
		return PT(X*co, Y*co);
	}
	PT operator / (ld co) const{
		return PT(X/co, Y/co);
	}
	ld operator * (PT ro) const{
		return X*ro.X + Y*ro.Y;
	}
	ld operator ^ (PT ro) const{
		return X*ro.Y - Y*ro.X;
	}
	void operator -= (PT ro){
		(*this) = (*this) - ro;
	}
};

istream& operator>> (istream &is, PT &p){
	ld val;
	is >> val;
	p.X = val;
	is >> val;
	p.Y = val;
	return is;
}

PT operator * (ld co, PT p){
	return p*co;
}

ld len(PT a){
	return sqrt(a*a);
}

PT unit(PT a){
	return a/len(a);
}

PT clrot(PT a){
	return PT(-a.Y, a.X);
}

PT proj(PT a, PT b, PT c){
	b = b-a, c = c-a;
	return a + (b*c)/(b*b)*b;
}

PT reflect(PT a, PT b, PT c){
	PT d = c;
	b = b-a, c = c-a;
	return d + (c^b)/len(b)*clrot(unit(b))*2;
}

int sgn(PT a, PT b, PT c){
	ld cr = (b-a)^(c-a);
	return cr > EPS ? 1 : (cr + EPS < 0 ? -1 : 0);
}

bool intersect(PT a, PT b, PT c, PT d){
	int as = sgn(c, d, a),
		bs = sgn(c, d, b),
		cs = sgn(a, b, c),
		ds = sgn(a, b, d);
	if (as && as == bs || cs && cs == ds)
		return false;
	else if (as || bs || cs || ds)
		return true;
	for (int j = 0; j < 2; j++){
		ld mX = min(a.X, b.X),
		   MX = max(a.X, b.X),
		   mY = min(a.Y, b.Y),
		   MY = max(a.Y, b.Y);
		for (int k = 0; k < 2; k++){
			if (c.X + EPS > mX && c.X < MX + EPS && c.Y + EPS > mY && c.Y < MY + EPS)
				return true;
			swap(c, d);
		}
		swap(a, c);
		swap(b, d);
	}
	return false;
}

PT intersection(PT a, PT b, PT c, PT d){
	ld c1 = (b-a)^(c-a), c2 = (b-a)^(d-a);
	return (c1*d - c2*c)/(c1 - c2);
}

