class Circle {
 public:
  Point c;
  double r;
  Circle(Point c = Point(), double r = 0.0) : c(c), r(r) {}

  int contains(const Point &p) const {
    double d = abs(c - p);
    if (equals(d, r)) return 1;  // ON
    if (d < r + EPS) return 2;   // IN
    return 0;                    // OUT
  }
};

