}

Point getCrossPoint(Segment s1, Segment s2){
    Vector base = s2.p2 - s2.p1;
    double d1 = cross(base, s1.p1 - s2.p1) < 0 ? -cross(base, s1.p1 - s2.p1) : cross(base, s1.p1 - s2.p1);
    double d2 = cross(base, s1.p2 - s2.p1) < 0 ? -cross(base, s1.p2 - s2.p1) : cross(base, s1.p2 - s2.p1);
    double t = d1 / (d1 + d2);
    return s1.p1 + (s1.p2 - s1.p1)*t;
}
