double norm(Point p){ return SQ(p.X) + SQ(p.Y); }

double dot(Point a, Point b){ return (conj(a) * b).X; }
double cross(Point a, Point b){ return (conj(a) * b).Y; }

enum CCW {
	COUNTER_CLOCKWISE = 1,
	CLOCKWISE = -1,
	ONLINE_BACK = 2,
	ONLINE_FRONT = -2,
	ON_SEGMENT = 0,
};

int ccw(Point p0, Point p1, Point p2){
	Point a = p1 - p0;
	Point b = p2 - p0;
	if (cross(a, b) > EPS) return COUNTER_CLOCKWISE;
	if (cross(a, b) < -EPS) return CLOCKWISE;
	if (dot(a, b) < -EPS) return ONLINE_BACK;
	if (LT(norm(a), norm(b))) return ONLINE_FRONT;
	return ON_SEGMENT;
}

bool orthogonal(Line l1, Line l2){
	return EQ(dot(l1.p2 - l1.p1, l2.p2 - l2.p1), 0.0);
}

bool parallel(Line l1, Line l2){
	return EQ(cross(l1.p2 - l1.p1, l2.p2 - l2.p1), 0.0);
}

Point project(Line l, Point p){
	Point base = l.p2 - l.p1;
	double r = dot(p - l.p1, base) / norm(base);
	return l.p1 + base * r;
}

Point reflect(Line &l, Point &p){
	return p + (project(l, p) - p) * 2.0;
}

bool sameLine(Line l1, Line l2){
	return abs(cross(l1.p2 - l1.p1, l2.p1 - l1.p1)) < EPS;
}

bool intersectLP(Line l, Point p){
	return LEQ(abs(cross(l.p1 - p, l.p2 - p)), 0.0);
}

bool intersectLL(Line l1, Line l2){
	return !parallel(l1, l2) || sameLine(l1, l2);
}

bool intersectLS(Line l, Segment s){
	return cross(l.p2 - l.p1, s.p1 - l.p1) * cross(l.p2 - l.p1, s.p2 - l.p1) < -EPS;
}

bool intersectSP(Segment s, Point p){
	return norm(s.p1 - p) + norm(s.p2 - p) - norm(s.p2 - s.p1) < EPS;
}

bool intersectSS(Segment s1, Segment s2){
	return ccw(s1.p1, s1.p2, s2.p1) * ccw(s1.p1, s1.p2, s2.p2) <= 0
		&& ccw(s2.p1, s2.p2, s1.p1) * ccw(s2.p1, s2.p2, s1.p2) <= 0;
}

double distanceLP(Line l, Point p){
	return abs(cross(l.p2 - l.p1, p - l.p1)) / abs(l.p2 - l.p1);
}

double distanceLL(Line l1, Line l2){
	if (intersectLL(l1, l2)) return 0.0;
	return distanceLP(l1, l2.p1);
}

double distanceLS(Line l, Segment s){
	if (intersectLS(l, s)) return 0.0;
	return min(distanceLP(l, s.p1), distanceLP(l, s.p2));
}

double distanceSP(Segment s, Point p){
	if (dot(s.p2 - s.p1, p - s.p1) < 0.0) return abs(p - s.p1);
	if (dot(s.p1 - s.p2, p - s.p2) < 0.0) return abs(p - s.p2);
	return distanceLP(s, p);
}

double distanceSS(Segment s1, Segment s2){
	if (intersectSS(s1, s2)) return 0.0;
	return min(
		min(distanceSP(s1, s2.p1), distanceSP(s1, s2.p2)),
		min(distanceSP(s2, s1.p1), distanceSP(s2, s1.p2))
	);
}

Point crossPointLL(Line l1, Line l2){
	assert(intersectLL(l1, l2));
	Point base = l2.p2 - l2.p1;
	double d1 = abs(cross(base, l1.p1 - l2.p1));
	double d2 = abs(cross(base, l1.p2 - l2.p1));
	double t = d1 / (d1 + d2);
	return l1.p1 + (l1.p2 - l1.p1) * t;
}

Point crossPointLS(Line l, Segment s){
	assert(intersectLS(l, s));
	return crossPointLL(l, s);
}

Point crossPointSS(Segment s1, Segment s2){
	assert(intersectSS(s1, s2));
	return crossPointLL(s1, s2);
}

enum {
	IN = 2,
	ON = 1,
	OUT = 0,
};

int contains(const Polygon &g, Point p){
	int n = g.size();
	bool x = false;
	rep(i, n){
		Point a = g[i] - p, b = g[(i + 1) % n] - p;
		if (abs(cross(a, b)) < EPS && dot(a, b) < EPS) return ON;
		if (a.Y > b.Y) swap(a, b);
		if (a.Y < EPS && EPS < b.Y && cross(a, b) > EPS) x = !x;
	}
	return x ? IN : OUT;
}

Polygon convexHull(Polygon &s){
	Polygon u, l;
	if (s.size() < 3) return s;
	sort(s.begin(), s.end());

	u.pb(s[0]); u.pb(s[1]);
	l.pb(s[s.size() - 1]); l.pb(s[s.size() - 2]);

	for (int i = 2; i < s.size(); i++){
		int n = u.size();
		while (n >= 2 && ccw(u[n - 2], u[n - 1], s[i]) != CLOCKWISE){
			u.pop_back();
			n--;
		}
		u.pb(s[i]);
	}

	for (int i = s.size() - 3; i >= 0; i--){
		int n = l.size();
		while (n >= 2 && ccw(l[n - 2], l[n - 1], s[i]) != CLOCKWISE){
			l.pop_back();
			n--;
		}
		l.pb(s[i]);
	}

	reverse(l.begin(), l.end());
	for (int i = u.size() - 2; i >= 1; i--) l.pb(u[i]);

	return l;
}

