}

using Vector = Point;

struct Segment
{
    Point p1, p2;

    Segment() = default;
    Segment(Point p1, Point p2) : p1(p1), p2(p2)
    {
    }
};

Point crossPoint(Segment &s1, Segment &s2)
{
    auto base = s2.p2 - s2.p1;

    auto d1 = fabs(base.cross(s1.p1 - s2.p1)) / base.abs();
    auto d2 = fabs(base.cross(s1.p2 - s2.p2)) / base.abs();

    auto t = d1 / (d1 + d2);

    return s1.p1 + (s1.p2 - s1.p1) * t;
}

