        long double a, b, c, d, e, f, g, h;
        cin >> a >> b >> c >> d;
        cin >> e >> f >> g >> h;
        CP p0(a, b), p1(c, d), p2(e, f), p3(g, h);
        CP ans = intersection_lines(p0, p1, p2, p3);
        cout << ans.real() << " " << ans.imag() << endl;
    }
}

