                l = ml;
            } else {
                r = mr;
            }
        }
        ext<T> ret;
        ret.x = l;
        ret.y = y;
        ret.val = f(l, y);
        return ret;
    };

    for (int i = 0; i < 100; i++) {
        T ml = (yl * 2 + yr) / 3.0;
        T mr = (yl + yr * 2) / 3.0;
        if (f(fx(ml, xl, xr).x, ml) < f(fx(mr, xl, xr).x, mr)) {
            yl = ml;
        } else {
            yr = mr;
        }
    }
    ext<T> ret;
    ret.x = fx(yl, xl, xr).x;
    ret.y = yl;
    ret.val = f(ret.x, ret.y);
    return ret;
}

double dist2(segment s, segment t) {
    ext<vec2> ans;
    function<double(vec2, vec2)> d = [&](vec2 a, vec2 b) {
        return norm2(b - a);
    };
    ans = ternary_2d(d, s.a, s.b, t.a, t.b, false);
    return ans.val;
}

vec2 intersection(segment s, segment t) {
    ext<vec2> ans;
    function<double(vec2, vec2)> d = [&](vec2 a, vec2 b) {
        return norm2(b - a);
    };
    ans = ternary_2d(d, s.a, s.b, t.a, t.b, false);
    assert(ans.val < EPS);
    return {ans.x.x, ans.x.y};
}

