    while(n --){
        cin >>p1.x >>p1.y >>p2.x >>p2.y>>p3.x >>p3.y >>p4.x >>p4.y;
        Point ans = cross_point(p1, p2, p3, p4);
        printf("%.10f %.10f\n", ans.x, ans.y);
    }
    return 0;
}

