Point crp(Point a1, Point a2, Point b1, Point b2) { 
	Point c; c.px = b2.px - b1.px; c.py = b2.py - b1.py; 
	Point d; d.px = a1.px - b1.px; d.py = a1.py - b1.py;
	Point e; e.px = a2.px - b1.px; e.py = a2.py - b1.py;
