    for( int i = 0; i < q; i++ )
    {
        cin >> xp0 >> yp0 >> xp1 >> yp1 >> xp2 >> yp2 >> xp3 >> yp3;

        double a, b, c, d, s, t, x, y;
        a = xp1 - xp0;
        b = yp1 - yp0;
        c = xp3 - xp2;
        d = yp3 - yp2;

        if( a != 0 && c != 0 )
        {
            s = yp0 - b * xp0 / a;
            t = yp2 - d * xp2 / c;
            if( b == 0 )
            {
                y = s;
                x = ( y - t ) / ( d / c );
            }
            else if( d == 0 )
            {
                y = t;
                x = ( y - s ) / ( b / a );
            }
            else
            {
                x = ( s - t ) / ( d / c - b / a );
                y = b / a * x + s;
            }
        }
        else if( a == 0 )
        {
            if( d == 0 )
            {
                x = xp0;
                y = yp2;
            }
            else
            {
                t = yp2 - d * xp2 / c;
                y = d * xp0 / c + t;
                x = ( y - t ) / ( d / c );
            }
        }
        else
        {
            if( b == 0 )
            {
                x = xp2;
                y = yp0;
            }
            else
            {
                s = yp0 - b * xp0 / a;
                x = xp2;
                y = b * x / a + s;
            }
        }
