    cin >> a >> b >> c >> d >> x >> y >> z >> w;
    P p0(a, b), p1(c, d), p2(x, y), p3(z, w);
    L l(p0, p1), m(p2, p3);

    P ans = crosspoint(l, m);
    cout << fixed << setprecision(10) << ans.real() << " " << ans.imag() << '\n';
  }
}

