struct line{
	pos p,q;
	pos vec;
	string str(){
		return ("("+p.str()+"-"+q.str()+")");
	}
	bool iscross(line a){
		double da,db;
		da=vec.cross( p.tov(a.p) );
		db=vec.cross( p.tov(a.q) );
		if(da*db>=0)return false;
		da=a.vec.cross( a.p.tov(p) );
		db=a.vec.cross( a.p.tov(q) );
		return (da*db<0);
	}
	line(pos ip,pos iq){
		p=ip; q=iq;
		vec=p.tov(q);
	}
	line(){}
	double dist(pos a){
		if(p.tov(a).dot( p.tov(q) )<0)return p.tov(a).norm;
		if(q.tov(a).dot( q.tov(p) )<0)return q.tov(a).norm;
		return abs(vec.cross(p.tov(a))/vec.norm);
	}
	double dist(line a){
		if(iscross(a))return 0;
		return min( min(dist(a.p),dist(a.q)) , min(a.dist(this->p),a.dist(this->q)));
	}
	pos crosspos(line a){
		pos v1=vec,v2=a.vec;
		pos pd=p-a.p;
		//printf("%s %s\n",v1.str().c_str(),v2.str().c_str());
		double d=1/(v1.x*v2.y-v2.x*v1.y);
		pos bp=pos(  v2.y*pd.x-v2.x*pd.y,
					-v1.y*pd.x+v1.x*pd.y );
		//printf("%lf %s\n",d,bp.str().c_str());
		double s=-bp.x*d;
		return (p+v1.scalar(s));	
	}
};

