Point Vec::IntersectionPoint( Vec Partner ) {
	if( (*this).isIntersection( Partner ) ) {
		//cout << Partner << endl;
		long double d1 = abs( Partner.Cross_product( Vec( Partner.getSP(), (*this).getSP() ) ).D.Z );
