        p3(Vec({x3, y3}));

    auto result =
        intersection(Line::fromPoints(p0, p1), Line::fromPoints(p2, p3));
    cout << fixed << setprecision(10) << result[0][0] << " " << result[0][1]
         << endl;
  }
  return 0;
}

