int ccw(Point a,Point b,Point c){
    b = b-a;
    c = c-a;
    if(cross(b,c) > 0)return +1; //a→b で反時計に折れてc ccw
    if(cross(b,c) < 0)return -1; //a→b で時計に折れてc ccw
    if(dot(b,c) < 0)return +2; // c--a--b;
    if(pabs(b)<pabs(c))return -2; //a--b--c
    return 0; //a--c--b
}
bool intersectSS(Line l,Line m){
    //lとmが交差してるかどうか
    return (ccw(l[0],l[1],m[0])*ccw(l[0],l[1],m[1])<=0)&&
            (ccw(m[0],m[1],l[0])*ccw(m[0],m[1],l[1])<=0);
}

double TriangleArea(Point a,Point b,Point c){
	b = b - a;
	c = c - a;
	return fabs(cross(b,c))*0.5;
}

double VertexArea(Polygon v){
	double subArea = 0.0;
	rep(i,v.size()-1){
		subArea += TriangleArea(v[0],v[i],v[i+1]);
	}
}

Point crosspoint(Line l,Line m){
	if(intersectSS(l,m) == false)return Point(INF,INF);
	return m[0]+(m[1]-m[0])*cross(l[1]-l[0],l[1]-m[0])/cross(l[1]-l[0],m[1]-m[0]);
}

