static const int Counter_CLOCKWISE = 1;
static const int CLOCKWISE = -1;
static const int ONLINE_BACK = 2;
static const int ONLINE_FRONT = -2;
static const int ON_SEGMENT = 0;

int ccw(Point p0, Point p1, Point p2){
  Vector a = p1 - p0;
  Vector b = p2 - p0;
  if(cross(a, b) > EPS) return Counter_CLOCKWISE;
  else if(cross(a, b) < -EPS) return CLOCKWISE;
  else if(dot(a, b) < -EPS) return ONLINE_BACK;
  else if(a.norm() < b.norm()) return ONLINE_FRONT;
  else return ON_SEGMENT;
}

bool intersect(Point p1, Point p2, Point p3, Point p4){
  return (ccw(p1, p2, p3) * ccw(p1, p2, p4) <= 0 &&
          ccw(p3, p4, p1) * ccw(p3, p4, p2) <= 0 );
}

double getDistance(Point a, Point b){
  Point tmp = a-b;
  return tmp.abs();
}

double getDistanceLP(Line l, Point p){
  Vector a = l.p2 - l.p1;
  Vector b = p - l.p1;
  return abs(cross(a, b) / a.abs());
}

double getDistanceSP(Segment s, Point p){
  Vector a = s.p2 - s.p1;
  Vector b = p - s.p1;
  double n = dot(a, b) / a.norm();
  if(0.0 <= n && n <= 1.0) return getDistanceLP(s, p);
  else return min(getDistance(s.p1, p), getDistance(s.p2, p));
}

double getDistanceSS(Segment s1, Segment s2){
  if(intersect(s1.p1, s1.p2, s2.p1, s2.p2)) return 0.0;
  double res = INF;
  chmin(res, getDistanceSP(s1, s2.p1));
  chmin(res, getDistanceSP(s1, s2.p2));
  chmin(res, getDistanceSP(s2, s1.p1));
  chmin(res, getDistanceSP(s2, s1.p2));
  return res;
}

Point getCrossPoint(Segment s1, Segment s2){
  double d1 = getDistanceLP(s1, s2.p1);
  double d2 = getDistanceLP(s1, s2.p2);
  double t = d1/(d1+d2);
  return s2.p1 + (s2.p2 - s2.p1)*t;
}

