const double eps = 1e-10;
typedef complex<double> Point;

ostream& operator<<(ostream& os, const Point& p){
    return os <<  p.real() << " " << p.imag();
}

inline double cross(const Point& a, const Point& b){
    return imag(conj(a) * b);
}

struct Line : vector<Point> {
    Line(const Point& a, const Point& b):vector<Point>{a, b}{}
};

inline bool interSP(const Line& s, const Point& p){
    return abs(s[0] - p) + abs(s[1] - p) - abs(s[1] - s[0]) < eps;
}

Point xptSS(const Line& s, const Line& t){
    double d = cross(s[1] - s[0], t[1] - t[0]);
    if(abs(d) < eps){
        if(interSP(t, s[0]))return s[0];
        if(interSP(t, s[1]))return s[1];
        if(interSP(s, t[0]))return t[0];
        return t[1];
    }
    return t[0] + (t[1] - t[0]) * cross(s[1] - s[0], s[1] - t[0]) * (1.0 / d);
}

