double DistanceSP(Segment s, Point p) {
	if (product(s.p2 - s.p1, p - s.p1) < 0.0) return abs(p, s.p1);
	if (product(s.p1 - s.p2, p - s.p2) < 0.0) return abs(p, s.p2);
	return DistanceLP(s, p);
}

double DistanceSS(Segment s1, Segment s2) {
	if (intersect(s1, s2)) return 0.0;
	return min(min(DistanceSP(s1, s2.p1), DistanceSP(s1,s2.p2)), min(DistanceSP(s2, s1.p1), DistanceSP(s2, s1.p2)));
}

Point CrossPoint(Segment s1,Segment s2) {
	double d1=DistanceSP(s2, s1.p1);
	double d2 = DistanceSP(s2, s1.p2);
	double t = d1 / (d1 + d2);
	return (s1.p1 + (s1.p2 - s1.p1)*t);

}

