typedef double coordinate_t;

struct coordinate {
  coordinate_t x, y;
  bool operator<(const coordinate & another) const {
    return x != another.x ? x < another.x : y < another.y;
  }
};

struct line {
  coordinate_t a;
  coordinate_t b;
  coordinate_t c;
};

// the intersections of 2 lines
coordinate cross_of_2_lines(line k, line l) {
  coordinate_t dn = l.a * k.b - k.a * l.b;
  if (dn == 0) return{ NAN, NAN };
  else {
    coordinate_t x = (l.b * k.c - k.b * l.c) / dn;
    coordinate_t y = (k.a * l.c - l.a * k.c) / dn;
    return{ x,y };
  }
}


// the intersections of n lines
vector<coordinate> crosses_of_lines(vector<line> lines) {
  int n = lines.size();
  set<coordinate> st;
  Loop(i, n) {
    Loop(j, i) {
      coordinate p = cross_of_2_lines(lines[i], lines[j]);
      if (p.x != NAN && p.y != NAN) st.insert(p);
    }
  }
  vector<coordinate> ret;
  for (auto itr = st.begin(); itr != st.end(); itr++) {
    ret.push_back(*itr);
  }
  return ret;
}


// the line through 2 coordinates
line line_through_2_coordinates(coordinate p, coordinate q) {
  if (p.x == q.x && p.y == q.y) return{ NAN, NAN, NAN };
  else {
    if (p.x == q.x) {
      return { 1, 0, -p.x };
    }
    else {
      return { (q.y - p.y), (p.x - q.x), q.x * p.y - p.x * q.y };
    }
  }
}



int main() {
  int q; cin >> q;
  cout << setprecision(18);
  Loop(i, q) {
    coordinate p, q;
    line s, t;
    cin >> p.x >> p.y >> q.x >> q.y;
    s = line_through_2_coordinates(p, q);
    cin >> p.x >> p.y >> q.x >> q.y;
    t = line_through_2_coordinates(p, q);
    coordinate r = cross_of_2_lines(s, t);
    cout << r.x << " " << r.y << endl;
  }
}

