struct Line
{
    Point p1,p2;
    Line(){}
    Line(Point a,Point b):p1(a),p2(b){}
    Point way() const{return p2-p1;}
    Point norm() const{return {p1.y-p2.y,p2.x-p1.x};}
    bool online(const Point &p) const{return sign((p-p1)%way())==0;}
    bool onsegment(const Point &p)const
    {
        return online(p) && sign((p-p1)*(p2-p))>=0;
    }
    pair<int,Point> intersect(const Line &I) const
    {
        if(sign(I.way()%way())==0) return {online(I.p1)?coin:para,{0,0}};
        const Point &p3=I.p1,&p4=I.p2;
        auto _143=(p1-p4)%(p4-p3),_342=(p3-p4)%(p4-p2);
        return {inte,(p1*_342+p2*_143)/(_143+_342)};
    }
    ld linedistance(const Point &p) const
    {
        return abs((p-p1)%way())/way().len();
    }
    Point project(const Point &p) const
    {
        return intersect(Line(p,p+norm())).second;
    }
    ld segmentdistance(const Point &p) const
    {
        const auto &pro=project(p);
        if(onsegment(pro)) return p.dis(pro);
        return min(p.dis(p1),p.dis(p2));
    }
};
