            >> x2 >> y2 >> x3 >> y3;
        Segment s1(Point(x0, y0), Point(x1, y1));
        Segment s2(Point(x2, y2), Point(x3, y3));

        Point ans = getCrossPoint(s1, s2);
        printf("%.9f %.9f\n", ans.x, ans.y);
    }

