	}
	if( (*this).getSP() == B.getGP() ) {
		return (*this).S_vec( B.Reverse_vec() );
	}
	if( (*this).getGP() == B.getGP() ) {
		return (*this).Reverse_vec().S_vec( B.Reverse_vec() );
	}

	return -1.L;
}

