// CGL_2_A
int parallel_orthogonal(C p0, C p1, C p2, C p3) {
    C a = (p3-p2) / (p1-p0);
    if (std::abs(a.real()) < EPS) { return 1; } // orthogonal
    if (std::abs(a.imag()) < EPS) { return 2; } // parallel
    return 0;
}

void solve_CGL_2_A() {
    int q;
    std::cin >> q;
    rep(i,q) {
        int xs[8];
        rep(k,8) {
