        C p1 = C(xs[2], xs[3]);
        C p2 = C(xs[4], xs[5]);
        C p3 = C(xs[6], xs[7]);
        int res = parallel_orthogonal(p0, p1, p2, p3);
        printf("%d\n", res);
    }
}

// CGL_2_B
bool intersection(C p0, C p1, C p2, C p3) {
    return
        ccw(p0, p1, p2) * ccw(p0, p1, p3) <= 0 &&
        ccw(p2, p3, p0) * ccw(p2, p3, p1) <= 0;
}

void solve_CGL_2_B() {
    int q;
    std::cin >> q;
