        }
        C p0 = C(xs[0], xs[1]);
        C p1 = C(xs[2], xs[3]);
        C p2 = C(xs[4], xs[5]);
        C p3 = C(xs[6], xs[7]);
        bool res = intersection(p0, p1, p2, p3);
        printf("%d\n", res ? 1 : 0);
    }
}

// CGL_2_C
C cross_point(C p0, C p1, C p2, C p3) {
    C p2_r = (p2-p0) / (p1-p0);
    C p3_r = (p3-p0) / (p1-p0);
    C p_r = (p2_r.imag() * p3_r - p3_r.imag() * p2_r) / (p2_r.imag() - p3_r.imag());
    C p = p_r * (p1-p0) + p0;
    return p;
}

void solve_CGL_2_C() {
    int q;
    std::cin >> q;
    rep(i,q) {
        int xs[8];
        rep(k,8) {
            std::cin >> xs[k];
        }
        C p0 = C(xs[0], xs[1]);
        C p1 = C(xs[2], xs[3]);
        C p2 = C(xs[4], xs[5]);
        C p3 = C(xs[6], xs[7]);
        C res = cross_point(p0, p1, p2, p3);
        printf("%.20lf %.20lf\n", res.real(), res.imag());
    }
}


