P cross(P a,P b,P c,P d)
{
    P ac=c-a,ab=b-a;
    P tmp=ac*conj(ab);
    double d1=tmp.real()/abs(ab);
    double d2=abs(tmp.imag()/abs(ab));
    P cd=d-c;
    tmp=cd*conj(ab);
    d2*=tmp.real()/abs(tmp.imag());
    return a+ab*(d1+d2)/abs(ab);
}

