    Point a,b,c,d,z;
    a=x.x;
    b=x.y;
    c=y.x;
    d=y.y;
    z.x=((b.x - a.x) * (c.x - d.x) * (c.y - a.y) -
         c.x * (b.x - a.x) * (c.y - d.y) + a.x * (b.y - a.y) * (c.x - d.x)) /
        ((b.y - a.y) * (c.x - d.x) - (b.x - a.x) * (c.y - d.y));
    z.y=((b.y - a.y) * (c.y - d.y) * (c.x - a.x) -
            c.y * (b.y - a.y) * (c.x - d.x) + a.y * (b.x - a.x) * (c.y - d.y))
        / ((b.x - a.x) * (c.y - d.y) - (b.y - a.y) * (c.x - d.x));
    return z;
}

