                            p*point(0, -1));
}

//point a, b, point c, d
point seg_to_seg_cross_point(line a, line c){
  if(not is_cross(a, c)) return point(1e9, 1e9);
  double xa = a.first.real(),  ya = a.first.imag();
  double xb = a.second.real(), yb = a.second.imag();
  double xc = c.first.real(),  yc = c.first.imag();
  double xd = c.second.real(), yd = c.second.imag();
  double s  = (ya - yc)*(xb - xa) + (yb - ya)*(xc - xa);
  double t  = (xb - xa)*(yd - yc) - (xd - xc)*(yb - ya);
  if(abs(t) < eps) return point(1e9, 1e9);
  return c.first + s*(c.second - c.first)/t;
}


