	for (int i = 0; i < N; i++) {
		cin >> A1.px >> A1.py >> A2.px >> A2.py;
		cin >> B1.px >> B1.py >> B2.px >> B2.py;
		Point G = crp(A1, A2, B1, B2);
		printf("%.114514Lf %.114514Lf\n", G.px, G.py);
	}
	return 0;
}

