void calcCrossPoint(
    double p1x, double p1y,
    double p2x, double p2y,
    double p3x, double p3y,
    double p4x, double p4y,
    double &cx, double &cy)
{
    double t = (p2y - p1y) * (p4x - p3x) - (p2x - p1x) * (p4y - p3y);
    cx = (p3y * p4x - p3x * p4y) * (p2x - p1x) - (p1y * p2x - p1x * p2y) * (p4x - p3x);
    cy = (p3y * p4x - p3x * p4y) * (p2y - p1y) - (p1y * p2x - p1x * p2y) * (p4y - p3y);
    cx /= t;
    cy /= t;
}

