		p1.x=a,p1.y=b,p2.x=c,p2.y=d;
	}
	segment(const point&a, const point&b):p1(a),p2(b){}
	point get_vec()const{return point(p2.x-p1.x,p2.y-p1.y);}
	double abs()const{return get_vec().abs();}
};
bool orthogonal(const vector&a,const vector&b){
	return fabs(dot(a,b))<EPS;
}
bool orthogonal(const point&a,const point&b,const point&c,const point&d){
	return fabs(dot(a-b,c-d))<EPS;
}
bool orthogonal(const segment&a,const segment&b){
	return fabs(dot(a.get_vec(),b.get_vec()))<EPS;
}
bool parallel(const vector&a,const vector&b){
	return fabs(cross(a,b))<EPS;
}
bool parallel(const point&a,const point&b,const point&c,const point&d){
	return fabs(cross(a-b,c-d))<EPS;
}
bool parallel(const segment&a,const segment&b){
	return fabs(cross(a.get_vec(),b.get_vec()))<EPS;
}
point project(const segment&s,const point&p){
	vector base=s.get_vec();
	double r=dot(p-s.p1,base)/base.norm();
	return s.p1+base*r;
}
point reflect(const segment&s,const point&p){
	return p+(project(s,p)-p)*2.0;
}
int ccw(const point&a,const point&b,const point&c){
	vector t1=b-a;
	vector t2=c-a;
	if(cross(t1,t2)>EPS) return 1;
	if(cross(t1,t2)<-EPS) return -1;
	if(dot(t1,t2)<-EPS) return -2;
	if(t1.norm()<t2.norm()) return 2;
	return 0;
}
bool intersect(const point&a,const point&b,const point&c,const point&d){
	return ((ccw(a,b,c)*ccw(a,b,d))<=0&&(ccw(c,d,a)*ccw(c,d,b))<=0);
}
bool intersect(const segment&a,const segment&b){
	return intersect(a.p1,a.p2,b.p1,b.p2);
}
point intersect_point(const segment&s1,const segment&s2){
	double d1=fabs(cross(s2.p1-s1.p1,s1.get_vec()));
	double d2=fabs(cross(s2.p2-s1.p1,s1.get_vec()));
	double t=d1/(d1+d2);
	return s2.p1+s2.get_vec()*t;
}
