class Segment {
public:
	Point p1, p2;

	Segment() {}

	//?????¨????°???±?????????
	Point projection(Point p) {
		Vector base = p2 - p1;

		double r = dot(p - p1, base) / base.norm();
		return p1 + base * r;
	}

	//????°??????????
	Point reflection(Point p) {
		return p + (projection(p) - p)*2.0;
	}


};


