bool Vec::onVecPoint( Point A ) {
	Vec Test( (*this).getSP(), A );
	if( A == ((*this).getSP()) || (Test.Parallel( *this ) && (*this).getD().X / Test.getD().X >= 1.0L) ) {
		return true;
	}
	return false;
}

