    cin>>n;
    for(int i=0;i<n;i++){
        cin>>x0>>y0>>x1>>y1>>x2>>y2>>x3>>y3;
        point s1,s2,t1,t2;
        line l,m;
        s1.x=x0; s1.y=y0;
        t1.x=x1; t1.y=y1;
        s2.x=x2; s2.y= y2;
        t2.x=x3; t2.y=y3;
        l.s=s1; l.t=t1;
        m.s=s2; m.t=t2;
        solve(l,m);
    }
}

