    static constexpr D Ae=0;
    D A(D a,D b){return a+b;}D Ainv(D a){return -a;}
    D S(D a,D b){return A(a,Ainv(b));}

    static constexpr D Me=1;
    D M(D a,D b){return a*b;}D Minv(D a){return 1.0/a;};

    int sig(D a,D b=0){return a<b-EPS?-1:a>b+EPS?1:0;}
    bool eq(D a,D b){ return sig(abs(a-b))==0;}

}
using namespace _double_tmpl;
namespace Ps{
    // using namespace _double_tmpl;
    typedef complex<D> P,Vec;

#define X real()
#define Y imag()
    istream& operator >> (istream& is,complex<D>& p){
        D x,y;is >> x >> y;p=P(x,y);return is;
    }

    bool eq(P a,P b){return eq(a.X,b.X) and eq(a.Y,b.Y);}
    // a×b
    D cross(const Vec& a,const Vec& b){return imag(conj(a)*b);}
    // a・b
    D dot(const Vec&a,const Vec& b) {return real(conj(a)*b);}

    int ccw(const P& a,P b,P c){
        b -= a; c -= a;
        if (cross(b,c) > EPS)   return +1;       // counter clockwise
        if (cross(b,c) < -EPS)   return -1;       // clockwise
        if (dot(b,c) < 0)     return +2;       // c--a--b on line
        if (norm(b) < norm(c)) return -2;       // a--b--c on line
        return 0;
    }
}
using namespace Ps;

namespace Ls{
    struct L : public vector<P> {
        Vec vec;
        L(const P &a, const P &b):vec(b - a){
            push_back(a); push_back(b);
        }
    };

    // 交点計算
    // verified by ACAC001 B
    // http://judge.u-aizu.ac.jp/onlinejudge/creview.jsp?rid=899115&cid=ACAC001
    P crosspoint(const L &l, const L &m) {
      D A = cross(l.vec, m.vec);
      D B = cross(l.vec, l[1] - m[0]);
      if (sig(A)==0 && sig(B)==0) return m[0]; // same line
      if (sig(A)==0) assert(false); //交点を持たない．
      return m[0] + B / A * (m[1] - m[0]);
    }
}
